mapper.select:

SELECT BASE_CD_NM AS "value"
FROM   WOASIS.TB_BASE_CD 
WHERE  CL_CD = #{clCd}
  AND  BASE_CD = #{baseCd}
  AND  USE_YN = 'Y'
ORDER BY IQ_SEQ, CL_CD, BASE_CD

SELECT CL_CD AS "clCd", --분류코드 BASE_CD AS "baseCd", --기초코드 BASE_CD_NM AS "baseCdNm", --기초코드명 SUPI_CL_CD AS "supiClCd", --상위분류코드 SUPI_BASE_CD AS "supiBaseCd", --상위기초코드 ETC_VAL_1 AS "etcVal1", --기타값1 ETC_VAL_2 AS "etcVal2", --기타값2 ETC_VAL_3 AS "etcVal3", --기타값3 REM_SBC AS "remSbc", --비고내용 IQ_SEQ AS "iqSeq", --조회순번 AFFR_SCN AS "affrScn", --완성창 업무구분 BASE_CD AS "code", BASE_CD_NM AS "value" FROM WOASIS.TB_BASE_CD WHERE CL_CD = #{code_group}
AND SUPI_CL_CD = #{supiClCd}
AND SUPI_BASE_CD = #{supiBaseCd}
AND USE_YN = 'Y' ORDER BY IQ_SEQ, CL_CD, BASE_CD


  SELECT BASE_CD AS "baseCd", ETC_VAL_1 AS "etcVal1"
    FROM WOASIS.TB_BASE_CD
   WHERE CL_CD = #{code_group} 
     AND BASE_CD = #{switch_type} 
     AND USE_YN = 'Y'
ORDER BY IQ_SEQ, CL_CD, BASE_CD

SELECT BASE_CD AS "code",
       BASE_CD_NM AS "value"
FROM   WOASIS.TB_BASE_CD 
WHERE  CL_CD = #{code_group}
  AND  BASE_CD >= #{start_st_cd}
  AND  BASE_CD <= #{end_st_cd}
  AND  USE_YN = 'Y'
  AND  BASE_CD NOT IN ('245', '250', '259', '295', '296', '297', '298')
ORDER BY IQ_SEQ, CL_CD, BASE_CD
