mapper.update:
   	/* updatePrjTmmrEnd 프로젝트 팀원 참여종료일 업데이트 */
   	
	UPDATE IRIS_PRJ_TMMR_INFO
	   SET PATC_END_DT = #{patcEndDt}
		 , LAST_MDFY_ID = #{_userId}
	     , LAST_MDFY_DT = getdate()
	 WHERE PRJ_CD = #{prjCd}
	   AND TMMR_EMP_NO = #{tmmrEmpNo}

mapper.insert:
   	
   	/* insertPrjTmmr 프로젝트 팀원 추가 */
   	INSERT INTO IRIS_PRJ_TMMR_INFO
	(
		  PRJ_CD
		, WBS_CD
		, TMMR_EMP_NO
		, SA_JOBX
		, PATC_STR_DT
		, PATC_END_DT
		, SA_FUNC
		, FRST_RGST_ID
		, FRST_RGST_DT
		, LAST_MDFY_ID
		, LAST_MDFY_DT
		, SA_SABUN_NAME
		, SA_JOBX_NAME
	)
	VALUES
	(
		  #{prjCd}
		, #{wbsCd}
		, #{tmmrEmpNo}
		, #{saJobX}
		, (SELECT convert(varchar(10),  GETDATE() ,23))
		, NULL
		, #{saFunC}
		, #{_userId}
		, getdate()
		, #{_userId}
		, getdate()
		, #{saSabunName}
		, #{saJobXname}
	)
