mapper.select:
-- getAttachFileInfo select MENU_TYPE as menuType ,ATTC_FIL_ID as attcFilId ,SEQ as seq ,FIL_NM as filNm ,FIL_PATH as filPath ,FIL_SIZE as filSize ,LAST_MDFY_ID as lastMdfyId from iris_com_attc_fil where attc_fil_id = #{attcFilId}
and seq = ${seq}
and seq in
${item}

mapper.insert:
		-- insertAttachFile 첨부파일 등록
		insert into iris_com_attc_fil
			(MENU_TYPE
			,ATTC_FIL_ID
			,SEQ
			,FIL_NM
			,FIL_PATH
			,FIL_SIZE
			,DEL_YN
			,FRST_RGST_ID
			,FRST_RGST_DT
			,LAST_MDFY_ID
			,LAST_MDFY_DT)
		values(#{menuType}
			  ,#{attcFilId}
			  ,${seq}
			  ,#{filNm}
			  ,#{filPath}
			  ,${filSize}
			  ,'N'
			  ,#{userId}
			  ,getdate()
			  ,#{userId}
			  ,getdate()
			  )

mapper.update:
	-- updateAttachFile
		update iris_com_attc_fil
		set	   del_yn = 'Y'
			  ,LAST_MDFY_ID = #{userId}
			  ,LAST_MDFY_DT = getdate()
		where  attc_fil_id = #{attcFilId}
		and	   seq = ${seq}

mapper.delete:
	-- deleteAttachFile
		delete	from iris_com_attc_fil
		where	attc_fil_id = #{attcFilId}
		and		seq = ${seq}
