mapper.select:
SELECT A.WBS_CD as "wbsCd" ,A.PRJ_CD as "prjCd" ,b.PRJ_NM as "prjNm" ,A.FXA_INFO_ID as "fxaInfoId" ,A.FXA_NM as "fxaNm" ,A.FXA_QTY as "fxaQty" ,A.FXA_UTM_NM as "fxaUtmNm" ,D.SA_NAME as "crgrNm" ,A.CRGR_ID as "crgrId" ,c.TRSF_APPR_DT as "trsfApprDt" ,A.OBT_PCE as "obtPce" ,A.BKP_PCE as "bkpPce" ,A.ATTC_FIL_ID as "attcFilId" ,A.FXA_LOC as "fxaLoc" ,A.TAG_YN as "tagYn" ,A.LAST_MDFY_ID as "lastMdfyId" FROM IRIS_FXA_INFO_MST A INNER JOIN IRIS_PRJ_RSST_MST B ON B.PRJ_CD = A.PRJ_CD INNER JOIN IRIS_SSO_USER D ON D.sa_sabun_new = A.CRGR_ID LEFT OUTER JOIN IRIS_FXA_TRSF_LIST C ON C.FXA_INFO_ID = A.FXA_INFO_ID WHERE A.FXA_ST_CD = 'USE'
AND A.WBS_CD = #{wbsCd}
AND B.PRJ_NM LIKE '%'+#{prjNm}+'%'
AND A.FXA_NM LIKE '%'+#{fxaNm}+'%'
AND A.FXA_NO = #{fxaNo}
AND D.SA_NAME LIKE '%'+#{crgrNm}+'%'
AND C.TRSF_APPR_DT BETWEEN #{fromDate} AND #{toDate}
