mapper.select:
SELECT B.FXA_NM AS "fxaNm" ,B.FXA_NO AS "fxaNo" ,B.FXA_QTY AS "fxaQty" ,B.FXA_UTM_NM AS "fxaUtmNm" ,ISNULL(SUBSTRING(c.PRJ_NM, 8, LEN(c.PRJ_NM)), DBO.FN_GETDEPTNAME(B.DEPT_CD)) as fromPrjNm ,dbo.fn_getSabunName(A.FROM_CRGR_ID) AS "fromCrgrNm" ,ISNULL(SUBSTRING(d.PRJ_NM, 8, LEN(d.PRJ_NM)), DBO.FN_GETDEPTNAME(B.DEPT_CD)) as "toPrjNm" ,dbo.fn_getSabunName(A.TO_CRGR_ID) AS "toCrgrNm" ,A.TRSF_RQ_DT AS "trsfRqDt" ,A.TRSF_APPR_DT AS "trsfApprDt" ,E.COM_DTL_NM AS "comDtlNm" ,E.COM_DTL_NM AS "trsfStNm" ,A.TRSF_ST_CD AS "trsfStCd" ,A.TRSF_RSON AS "trsfRson" ,A.FROM_CRGR_ID AS "fromCrgrId" ,A.TO_CRGR_ID AS "toCrgrId" ,A.FXA_TRSF_ID AS "fxaTrsfId" ,A.FXA_INFO_ID AS "fxaInfoId" FROM IRIS_FXA_TRSF_LIST A INNER JOIN IRIS_FXA_INFO_MST B ON A.FXA_INFO_ID = B.FXA_INFO_ID INNER JOIN IRIS_WBS_PJT_IF C ON C.WBS_CD = A.FROM_WBS_CD INNER JOIN IRIS_WBS_PJT_IF D ON D.WBS_CD = A.TO_WBS_CD ,IRIS_ADM_COM_CD E WHERE E.COM_CD_CD = 'TRSF_ST_CD' AND E.COM_DTL_CD = A.TRSF_ST_CD AND A.DEL_YN = 'N'
AND B.WBS_CD = #{wbsCd}
AND B.FXA_NM LIKE '%'+#{fxaNm}+'%'
AND B.FXA_NO = #{fxaNo}
AND (C.PRJ_NM LIKE '%'+#{prjNm}+'%' OR D.PRJ_NM LIKE '%'+#{prjNm}+'%')

mapper.insert:
INSERT INTO IRIS_FXA_TRSF_LIST
	(
	 FXA_INFO_ID
	,FROM_DEPT_CD
	,FROM_WBS_CD
	,FROM_CRGR_ID
	,TO_DEPT_CD
	,TO_WBS_CD
	,TO_CRGR_ID
	,TRSF_ST_CD
	,TRSF_RSON
	,TRSF_RQ_DT
	,DEL_YN
	,FRST_RGST_DT
	,FRST_RGST_ID
	,LAST_MDFY_DT
	,LAST_MDFY_ID
	)
	VALUES
	(
	 #{fxaInfoId}
	,#{fromDeptCd}
	,#{fromWbsCd}
	,#{fromCrgrId}
	,#{toDeptCd}
	,#{toWbsCd}
	,#{toCrgrId}
	,'RQ'
	,#{trsfRson}
	,CONVERT(NVARCHAR(10), GETDATE(), 23)
	,'Y'
	,GETDATE()
	,#{_userId}
	,GETDATE()
	,#{_userId}
	)
