mapper.update:
-- saveNrmInfo  규격 등록 및 업데이트
merge into IRIS_KNLD_NRM m
using   ( select #{nrmNo} as nrmNo ) as tab1 
on      ( m.NRM_NO = tab1.nrmNo )
WHEN MATCHED THEN
UPDATE SET  NRM_NO           = #{nrmNo}
           ,NRM_NM           = #{nrmNm}
           ,ISS_AUTH         = #{issAuth}
           ,APLC_NO          = #{aplcNo}
           ,APLC_NM          = #{aplcNm}
           ,GET_DT           = #{getDt}
           ,ENCT_DT          = #{enctDt}
           ,LAST_RFRM_DT     = #{lastRfrmDt}
           ,SUBSIDIARY_INFO  = #{subsidiaryInfo}
           ,LANG             = #{lang}
           ,PCE              = #{pce}
           ,PAGE_CNT         = #{pageCnt}
           ,ABST             = #{abst}
           ,ATTC_FILE_ID     = #{attcFileId}
           ,LAST_MDFY_DT     = getdate()
           ,LAST_MDFY_ID     = #{userId}
WHEN NOT MATCHED THEN
INSERT     (NRM_NO
           ,NRM_NM
           ,ISS_AUTH
           ,APLC_NO
           ,APLC_NM
           ,GET_DT
           ,ENCT_DT
           ,LAST_RFRM_DT
           ,SUBSIDIARY_INFO
           ,LANG
           ,PCE
           ,PAGE_CNT
           ,ABST
           ,ATTC_FILE_ID
           ,DEL_YN
           ,FRST_RGST_DT
           ,FRST_RGST_ID
           ,LAST_MDFY_DT
           ,LAST_MDFY_ID
           )
     VALUES
           (
            #{nrmNo}
           ,#{nrmNm}
           ,#{issAuth}
           ,#{aplcNo}
           ,#{aplcNm}
           ,#{getDt}
           ,#{enctDt}
           ,#{lastRfrmDt}
           ,#{subsidiaryInfo}
           ,#{lang}
           ,#{pce}
           ,#{pageCnt}
           ,#{abst}
           ,#{attcFileId}
           ,'N'
           ,getdate()
           ,#{userId}
           ,getdate()
           ,#{userId}
		   );
