mapper.insert:
	
		insert into IRIS_KNLD_CFRN
		(			 TITL_NM
					,CFRN_LOC_SCN_CD
					,CFRN_STRT_DT
					,CFRN_FNH_DT
					,SBC_NM
					,RTRV_CNT
					,KEYWORD_NM
		
		            
					,ATTC_FILE_ID
		            
    
					,RGST_ID
					,RGST_OPS_ID
					,DEL_YN
					, FRST_RGST_ID
					, FRST_RGST_DT
					, LAST_MDFY_ID
					, LAST_MDFY_DT
		)
		values
		(
		      #{titlNm}
		    , #{cfrnLocScnCd}
		    , #{cfrnStrtDt}
		    , #{cfrnFnhDt}
		    , #{sbcNm}
		    , 0
		    , #{keywordNm}
		    
			
		    , #{attcFilId}
		    
		     
		    , #{_userId}
		    , ( SELECT sa_dept_new FROM IRIS_SSO_USER WHERE sa_user = #{_userId} )
		    , 'N'
		    , #{_userId}
		    , getdate()
		    , #{_userId}
		    , getdate()
		)

mapper.update:
	
		UPDATE IRIS_KNLD_CFRN
		   SET LAST_MDFY_ID = #{_userId}
		     , LAST_MDFY_DT = getdate()
		     , TITL_NM = #{titlNm}
		     , CFRN_LOC_SCN_CD = #{cfrnLocScnCd}
	         , CFRN_STRT_DT = #{cfrnStrtDt}
	         , CFRN_FNH_DT = #{cfrnFnhDt}
		     , SBC_NM = #{sbcNm}
		     , KEYWORD_NM = #{keywordNm}
	
		
			, ATTC_FILE_ID = #{attcFilId}
	    

	
		 WHERE CFRN_ID = #{conferenceId}

	
		UPDATE IRIS_KNLD_CFRN
		   SET RTRV_CNT = RTRV_CNT + 1
	

	
		 WHERE CFRN_ID = #{conferenceId}

	
		UPDATE IRIS_KNLD_CFRN
		   SET DEL_YN = 'Y'
	

	
		 WHERE CFRN_ID = #{conferenceId}
