mapper.insert:

-- insertSaftyInfo 안전환경보건 저장
	insert into IRIS_KNLD_SFTY_ENV
	(			 TITL_NM
				,SFT_ENV_SCN_CD
				,ENFC_DT
				,RFRM_SBC
				,SBC_NM
				,RTRV_CNT
				,KEYWORD_NM


				,ATTC_FILE_ID


				,RGST_ID
				,RGST_OPS_ID
				,DEL_YN
				, FRST_RGST_ID
				, FRST_RGST_DT
				, LAST_MDFY_ID
				, LAST_MDFY_DT
	)
	values
	(
	      #{titlNm}
	    , #{sftEnvScnCd}
	    , #{enfcDt}
	    , #{rfrmSbc}
	    , #{sbcNm}
	    , 0
	    , #{keywordNm}


	    , #{attcFilId}


	    , #{_userId}
	    , ( SELECT sa_dept_new FROM IRIS_SSO_USER WHERE sa_user = #{_userId} )
	    , 'N'
	    , #{_userId}
	    , getdate()
	    , #{_userId}
	    , getdate()
	)

mapper.update:

-- updateSaftyInfo 안전환경보건 업데이트
	UPDATE IRIS_KNLD_SFTY_ENV
	   SET LAST_MDFY_ID = #{_userId}
	     , LAST_MDFY_DT = getdate()
	     , TITL_NM = #{titlNm}
	     , SFT_ENV_SCN_CD = #{sftEnvScnCd}
         , ENFC_DT = #{enfcDt}
         , RFRM_SBC = #{rfrmSbc}
	     , SBC_NM = #{sbcNm}
	     , KEYWORD_NM = #{keywordNm}


		, ATTC_FILE_ID = #{attcFilId}



	 WHERE SFT_ENV_ID = #{saftyId}
