mapper.insert:
	
		insert into IRIS_KNLD_EDU_SMN
		(			 TITL_NM
					,EDU_PL_SCN_CD
					,EDU_INST_NM
					,EDU_STRT_DT
					,EDU_FNH_DT
					,SBC_NM
					,RTRV_CNT
					,KEYWORD_NM
		
		            
					,ATTC_FILE_ID
		            
    
					,RGST_ID
					,RGST_OPS_ID
					,DEL_YN
					, FRST_RGST_ID
					, FRST_RGST_DT
					, LAST_MDFY_ID
					, LAST_MDFY_DT
		)
		values
		(
		      #{titlNm}
		    , #{eduPlScnCd}
		    , #{eduInstNm}
		    , #{eduStrtDt}
		    , #{eduFnhDt}
		    , #{sbcNm}
		    , 0
		    , #{keywordNm}
		    
			
		    , #{attcFilId}
		    
		     
		    , #{_userId}
		    , ( SELECT sa_dept_new FROM IRIS_SSO_USER WHERE sa_user = #{_userId} )
		    , 'N'
		    , #{_userId}
		    , getdate()
		    , #{_userId}
		    , getdate()
		)

mapper.update:
	
		UPDATE IRIS_KNLD_EDU_SMN
		   SET LAST_MDFY_ID = #{_userId}
		     , LAST_MDFY_DT = getdate()
		     , TITL_NM = #{titlNm}
		     , EDU_PL_SCN_CD = #{eduPlScnCd}
		     , EDU_INST_NM = #{eduInstNm}
	         , EDU_STRT_DT = #{eduStrtDt}
	         , EDU_FNH_DT = #{eduFnhDt}
		     , SBC_NM = #{sbcNm}
		     , KEYWORD_NM = #{keywordNm}
	
		
			, ATTC_FILE_ID = #{attcFilId}
	    

	
		 WHERE EDU_SMN_ID = #{eduId}

	
		UPDATE IRIS_KNLD_EDU_SMN
		   SET RTRV_CNT = RTRV_CNT + 1
	

	
		 WHERE EDU_SMN_ID = #{eduId}

	
		UPDATE IRIS_KNLD_EDU_SMN
		   SET DEL_YN = 'Y'
	

	
		 WHERE EDU_SMN_ID = #{eduId}
