mapper.insert:

	-- insertPubNoticeInfo 공지사항 저장
		insert into IRIS_KNLD_PWI_IMTR
		(     TITL_NM
	        , PWI_SCN_CD
			, SBC_NM
			, RTRV_CNT
			, KEYWORD_NM


            , ATTC_FILE_ID


			, UGY_YN
			, RGST_ID
			, RGST_OPS_ID
			, DEL_YN
			, FRST_RGST_ID
			, FRST_RGST_DT
			, LAST_MDFY_ID
			, LAST_MDFY_DT
		)
		values
		(
		      #{titlNm}
		    , #{pwiScnCd}
		    , #{sbcNm}
		    , 0
		    , #{keywordNm}


		    , #{attcFilId}

		    , #{ugyYn}

		    , #{_userId}
		    , ( SELECT sa_dept_new FROM IRIS_SSO_USER WHERE sa_user = #{_userId} )
		    , 'N'
		    , #{_userId}
		    , getdate()
		    , #{_userId}
		    , getdate()
		)

mapper.update:

-- updatePubNoticeInfo 공지사항 업데이트
	UPDATE IRIS_KNLD_PWI_IMTR
	   SET LAST_MDFY_ID = #{_userId}
	     , LAST_MDFY_DT = getdate()
	     , TITL_NM = #{titlNm}
	     , PWI_SCN_CD = #{pwiScnCd}
	     , UGY_YN = #{ugyYn}
	     , SBC_NM = #{sbcNm}
	     , KEYWORD_NM = #{keywordNm}
	     , ATTC_FILE_ID = #{attcFilId}




	 WHERE PWI_ID = #{pwiId}
