mapper.insert:

insert into IRIS_KNLD_QNA
(			 TITL_NM
			,QNA_CL_CD
			,QUST_CL_CD
			,RLTD_WRTG_ID
			,SBC_NM
			,RTRV_CNT
			,KEYWORD_NM


			,ATTC_FILE_ID


			,RGST_ID
			,RGST_OPS_ID
			,DEL_YN
			, FRST_RGST_ID
			, FRST_RGST_DT
			, LAST_MDFY_ID
			, LAST_MDFY_DT
)
values
(
      #{titlNm}
    , #{qnaClCd}
    , #{qustClCd}
    , #{rltdWrtgId}
    , #{sbcNm}
    , 0
    , #{keywordNm}


    , #{attcFilId}


    , #{_userId}
    , ( SELECT sa_dept_new FROM IRIS_SSO_USER WHERE sa_user = #{_userId} )
    , 'N'
    , #{_userId}
    , getdate()
    , #{_userId}
    , getdate()
)

mapper.update:

	UPDATE IRIS_KNLD_QNA
	   SET LAST_MDFY_ID = #{_userId}
	     , LAST_MDFY_DT = getdate()
	     , TITL_NM = #{titlNm}
	     , QNA_CL_CD = #{qnaClCd}
         , QUST_CL_CD = #{qustClCd}
         , RLTD_WRTG_ID = #{rltdWrtgId}
	     , SBC_NM = #{sbcNm}
	     , KEYWORD_NM = #{keywordNm}


		, ATTC_FILE_ID = #{attcFilId}



	 WHERE QNA_ID = #{qnaId}

mapper.insert:

