mapper.insert:
	
		insert into IRIS_KNLD_RSST_DOC
		(
			  AFFR_CL_ID
			, A.AFFR_CL_GROUP
			, TITL_NM
			, SBC_NM
			, RTRV_CNT
			, KEYWORD_NM
			
			
            , ATTC_FILE_ID
            
            
			, RGST_ID
			, RGST_OPS_ID
			, DEL_YN
			, FRST_RGST_ID
			, FRST_RGST_DT
			, LAST_MDFY_ID
			, LAST_MDFY_DT
		)
		values
		(
		      #{affrClId}
		    , #{affrClGroup}
		    , #{titlNm}
		    , #{sbcNm}
		    , 0
		    , #{keywordNm}
		    
			
            , #{attcFilId}
            
            
		    , #{_userId}
		    , ( SELECT sa_dept_new FROM IRIS_SSO_USER WHERE sa_user = #{_userId} )
		    , 'N'
		    , #{_userId}
		    , getdate()
		    , #{_userId}
		    , getdate()
		)

mapper.update:

	UPDATE IRIS_KNLD_RSST_DOC
	   SET LAST_MDFY_ID = #{_userId}
	     , LAST_MDFY_DT = getdate()
	     , AFFR_CL_ID = #{affrClId}
	     , TITL_NM = #{titlNm}
	     , SBC_NM = #{sbcNm}
	     , KEYWORD_NM = #{keywordNm}

	
		, ATTC_FILE_ID = #{attcFilId}
    

	 WHERE RSST_DOC_ID = #{prdtId}

	
		UPDATE IRIS_KNLD_RSST_DOC
		   SET RTRV_CNT = RTRV_CNT + 1
	

	
		 WHERE RSST_DOC_ID = #{prdtId}

	
		UPDATE IRIS_KNLD_RSST_DOC
		   SET DEL_YN = 'Y'
	

	
		 WHERE RSST_DOC_ID = #{prdtId}
