mapper.update:
-- saveMachineMgmtInfo
	MERGE IRIS_MCHN_INFO_MGMT MIG
	USING ( SELECT #{infoMgmtId} AS INFO_MGMT_ID ) AS Tab1
	ON  (MIG.INFO_MGMT_ID = Tab1.INFO_MGMT_ID)
	WHEN matched THEN
	UPDATE SET
	       MCHN_INFO_ID   = #{mchnInfoId}
	      ,MGMT_DT        = #{mgmtDt}
	      ,DTL_CL_CD      = #{dtlClCd}
	      ,RGST_ID        = #{rgstId}
	      ,MCHN_USE_PSBL_YN = #{mchnUsePsblYn}
	      ,MGMT_UFE       = #{mgmtUfe}
	      ,SMRY_SBC       = #{smrySbc}
	      ,DTL_SBC        = #{dtlSbc}
	      ,ATTC_FIL_ID    = #{attcFilId}
	      ,LAST_MDFY_DT   = GETDATE()
	      ,LAST_MDFY_ID   = #{_userId}
	WHEN not matched THEN
	INSERT
	       ( MCHN_INFO_ID
			,MGMT_DT
			,DTL_CL_CD
			,RGST_DT
			,RGST_ID
			,MCHN_USE_PSBL_YN
			,MGMT_UFE
			,SMRY_SBC
			,DTL_SBC
			,ATTC_FIL_ID
		    ,DEL_YN
		    ,FRST_RGST_DT
		    ,FRST_RGST_ID
		    ,LAST_MDFY_DT
		    ,LAST_MDFY_ID)
	    VALUES
	       ( #{mchnInfoId}
			,#{mgmtDt}
			,#{dtlClCd}
			,CONVERT(nvarchar(10), GETDATE(), 23)
			,#{rgstId}
			,#{mchnUsePsblYn}
			,#{mgmtUfe}
			,#{smrySbc}
			,#{dtlSbc}
			,#{attcFilId}
			,'N'
			,GETDATE()
			,#{_userId}
			,GETDATE()
			,#{_userId});

-- updateMachineInfo
	UPDATE IRIS_MCHN_INFO_MST
	SET    MCHN_USE_PSBL_YN = #{mchnUsePsblYn}
	      ,LAST_MDFY_DT   	= GETDATE()
	      ,LAST_MDFY_ID   	= #{_userId}
	WHERE MCHN_INFO_ID 	  	= #{mchnInfoId}
