mapper.select:
SELECT * FROM ( SELECT A.MCHN_INFO_ID AS mchnInfoId ,A.TOOL_NM AS mchnHanNm ,A.TOOL_NM AS mchnNm ,dbo.fn_getComDtlNm('MCHN_PRCT_ST' , A.MCHN_USE_PSBL_YN) as mchnUsePsblNm ,A.MCHN_USE_PSBL_YN AS mchnUsePsblYn ,A.MDL_NM AS mdlNm ,A.MKR_NM AS mkrNm ,A.MCHN_EXPL AS mchnExpl ,A.OPN_YN AS opnYn ,CASE WHEN OPN_YN = 'Y' THEN 'OPEN' ELSE '' END opnNm ,CONCAT( SU.SA_NAME, ' ', SU.sa_jobx_name) AS crgrNm ,SU.SA_DEPT_NAME AS deptNm ,F.ATTC_FIL_ID AS attcFilId ,F.SEQ AS seq ,A.MCHN_CL_CD AS mchnClCd ,dbo.fn_getEvWay(A.MCHN_INFO_ID) AS evWay ,A.VER AS ver ,CASE WHEN CHARINDEX('열/에너지', dbo.fn_getEvWay(A.MCHN_INFO_ID)) > 0 THEN '1' WHEN CHARINDEX('환기/공기질', dbo.fn_getEvWay(A.MCHN_INFO_ID)) > 0 THEN '2' WHEN CHARINDEX('빛', dbo.fn_getEvWay(A.MCHN_INFO_ID)) > 0 THEN '3' WHEN CHARINDEX('구조', dbo.fn_getEvWay(A.MCHN_INFO_ID)) > 0 THEN '4' ELSE '5' END AS ord FROM IRIS_SPACE_MCHN_INFO_MGMT A LEFT OUTER JOIN IRIS_COM_ATTC_FIL F ON F.ATTC_FIL_ID = A.ATTC_FIL_ID AND F.DEL_YN = 'N' LEFT OUTER JOIN IRIS_SSO_USER SU ON SU.SA_SABUN_NEW = A.MCHN_CRGR_ID WHERE A.DEL_YN = 'N'
AND A.TOOL_NM LIKE '%'+#{srhInput}+'%'
AND SU.SA_NAME LIKE '%'+#{srhInput}+'%'
AND A.MCHN_CL_CD = #{mchnClCd}
AND A.OPN_YN = #{opnYn}
) B ORDER BY mchnClCd, ord, mchnNm

mapper.update:

	MERGE IRIS_SPACE_MCHN_PRCT_MST MEM
		USING ( SELECT #{mchnPrctId} AS MCHN_PRCT_ID ) AS Tab1
		ON  (MEM.MCHN_PRCT_ID = Tab1.MCHN_PRCT_ID)
		WHEN MATCHED THEN
UPDATE SET	PRCT_TITL	   = #{prctTitl}
		   ,LAST_MDFY_DT = GETDATE()
		   ,LAST_MDFY_ID = #{_userId}
	WHEN NOT MATCHED THEN
	INSERT	 (  MCHN_INFO_ID
		   ,PRCT_TITL
		   ,RGST_ID
		   ,PRCT_FROM_DT
		   ,PRCT_TO_DT
		   ,PRCT_SCN_CD
		   ,DEL_YN
		   ,FRST_RGST_DT
		   ,FRST_RGST_ID
		   ,LAST_MDFY_DT
		   ,LAST_MDFY_ID)
	 VALUES
		   (#{mchnInfoId}
		   ,#{prctTitl}
		   ,#{rgstId}
		   ,#{prctFromDt}
		   ,#{prctToDt}
		   ,'RQ'
		   ,'N'
		   ,GETDATE()
		   , #{_userId} 
		   ,GETDATE()
		   , #{_userId}
		);


UPDATE IRIS_SPACE_MCHN_PRCT_MST
SET    DEL_YN = 'Y'
      ,LAST_MDFY_DT = GETDATE()
	  ,LAST_MDFY_ID = #{_userId} 
WHERE  MCHN_PRCT_ID = #{mchnPrctId}
