mapper.update:
-- updateTrwiBudgPro
 UPDATE iris_tss_gen_trwi_budg_edit_info
       SET PSNN_RISN_PRO  = #{psnnRisnPro}
         , PCE_RISN_PRO	  = #{pceRisnPro}
         , LAST_MDFY_DT = GETDATE()
         , LAST_MDFY_ID = #{userId}
   WHERE  YY = #{budgYY}

mapper.delete:
-- deleteTrwiBudg
	delete from IRIS_TSS_GEN_TRWI_BUDG_EDIT_INFO
	where yy = #{budgYY}
