mapper.insert:
-- insertMmCls IRIS_MM_CLS 테이블 저장(M/M 입력 참여율저장) INSERT INTO IRIS_MM_CLS ( TSS_CD , PRJ_CD , SA_SABUN_NEW , PTC_PRO /*참여율*/ , MM_YYMM , COMM_TXT , OUSD_TSS_YN /*타과제여부*/ , CLS_YN /*마감여부*/ , ILCK_ST /*연동상태*/ , FRST_RGST_ID , FRST_RGST_DT , LAST_MDFY_ID , LAST_MDFY_DT , WBS_CD
, TO_DO_YN
) VALUES ( #{tssCd} , #{prjCd} , #{saSabunNew} , ISNULL(#{ptcPro},0) , #{mmYymm} , #{commTxt} , ISNULL(#{ousdTssYn},'N') , 'N' , ISNULL(#{ilckSt},'N') , #{_userId} , getdate() , #{_userId} , getdate() , #{tssWbsCd}
, #{toDoYn}
)

mapper.update:

-- updateMmCls IRIS_MM_CLS 테이블 업데이트(M/M 입력 참여율 수정)
    UPDATE IRIS_MM_CLS
           SET LAST_MDFY_ID = #{_userId}
             , LAST_MDFY_DT = getdate()

        
             , PTC_PRO = #{ptcPro}
        
        
             , COMM_TXT = #{commTxt}
        
        
             , OUSD_TSS_YN = #{ousdTssYn}
        
        
             , ILCK_ST = #{ilckSt}
        
        
             , CLS_YN = #{clsYn}
        
        
             , CLS_DT = CONVERT(NVARCHAR(10), GETDATE(), 23)
        
        
            , TO_DO_YN = #{toDoYn}
        

     WHERE TSS_CD = #{tssCd}
       AND SA_SABUN_NEW = #{saSabunNew}
       AND MM_YYMM = #{mmYymm}
       and WBS_CD = #{tssWbsCd}

    
    -- updateMmClsIlck IRIS_MM_CLS  연동
        update iris_mm_cls
        set    ILCK_ST =  'Y'
               ,LAST_MDFY_DT = getdate()
               ,LAST_MDFY_ID = #{_userId}
        where  PRJ_CD = #{prjCd}
        and    TSS_CD = #{tssCd}
        and    MM_YYMM = #{mmYymm}
        and    sa_sabun_new = #{saSabunNew}
        /*and    wbs_cd = tssWbsCd -- [20240429.siseo]update 오류로 인하여 주석으로 막음 */

mapper.insert:
    
    -- saveMmCls IRIS_MM_CLS 테이블 저장(저장/수정)
        MERGE INTO IRIS_MM_CLS A
            USING ( SELECT    #{saSabunNew} as SA_SABUN_NEW,
                            #{mmYymm}     as MM_YYMM,
                            #{tssCd}      as TSS_CD,
                            #{tssWbsCd}   as WBS_CD
                            ) B
            ON ( A.SA_SABUN_NEW = B.SA_SABUN_NEW and A.MM_YYMM = B.MM_YYMM and A.TSS_CD = B.TSS_CD and a.WBS_CD = b.WBS_CD)
            WHEN MATCHED THEN
                UPDATE
                   SET LAST_MDFY_ID = #{_userId}
                     , LAST_MDFY_DT = getdate()

        
                
                     , WBS_CD =  #{tssWbsCd}
                
                
                     , PTC_PRO = #{ptcPro}
                
                
                     , COMM_TXT = #{commTxt}
                
                
                     , OUSD_TSS_YN = #{ousdTssYn}
                
                
                     , ILCK_ST = #{ilckSt}
                
                
                     , CLS_YN = #{clsYn}
                
                
                     , CLS_DT = CONVERT(NVARCHAR(10), GETDATE(), 23)
                
                
                    , TO_DO_YN = #{toDoYn}
                
        
            WHEN NOT MATCHED THEN
                INSERT
                (
                   TSS_CD
                 , PRJ_CD
                 , SA_SABUN_NEW
                 , PTC_PRO          /*참여율*/
                 , MM_YYMM
                 , COMM_TXT
                 , OUSD_TSS_YN      /*타과제여부*/
                 , CLS_YN           /*마감여부*/
                 , ILCK_ST          /*연동상태*/
                 , FRST_RGST_ID
                 , FRST_RGST_DT
                 , LAST_MDFY_ID
                 , LAST_MDFY_DT
                 , WBS_CD
        
                 
                 , CLS_DT
                 
                 
                 , TO_DO_YN
                 
        
                )
                VALUES
                (
                   #{tssCd}
                 , #{prjCd}
                 , #{saSabunNew}
                 , ISNULL(#{ptcPro},0)
                 , #{mmYymm}
                 , #{commTxt}
                 , ISNULL(#{ousdTssYn},'N')
                 , ISNULL(#{clsYn},'N')
                 , ISNULL(#{ilckSt},'N')
                 , #{_userId}
                 , getdate()
                 , #{_userId}
                 , getdate()
                 , #{tssWbsCd}
        
                 
                 , CONVERT(NVARCHAR(10), GETDATE(), 23)
                 
                 
                 , #{toDoYn}
                 
        
                );
