mapper.delete:

-- deletePrjPtotPrptInfo 지적재산권 삭제
     DELETE FROM IRIS_PRJ_PTOT_PRPT
      WHERE PRPT_ID = #{prptId}
        AND PRJ_CD = #{prjCd}
        AND WBS_CD = #{wbsCd}

mapper.insert:

-- insertPrjPtotPrptInfo 지적재산권 등록
	INSERT INTO IRIS_PRJ_PTOT_PRPT
	(
	   PRJ_CD
	 , WBS_CD
	 , PRPT_GOAL_YEAR
	 , PRPT_GOAL_CNT
	 , FRNW_SCN
	 , FRST_RGST_ID
	 , FRST_RGST_DT
	 , LAST_MDFY_ID
	 , LAST_MDFY_DT
	)
	VALUES
	(
	   #{prjCd}
	 , #{wbsCd}
	 , #{prptGoalYear}
	 , #{prptGoalCnt}
	 , #{frnwScn}
	 , #{_userId}
	 , getdate()
	 , #{_userId}
	 , getdate()
	)

mapper.update:

-- updatePrjPtotPrptInfo 지적재산권 수정
	UPDATE IRIS_PRJ_PTOT_PRPT
       SET LAST_MDFY_ID = #{_userId}
	     , LAST_MDFY_DT = getdate()


	     , PRPT_GOAL_YEAR = #{prptGoalYear}


	     , PRPT_GOAL_CNT = #{prptGoalCnt}


	     , FRNW_SCN = #{frnwScn}


	 WHERE PRPT_ID = #{prptId}
	   AND PRJ_CD = #{prjCd}
       AND WBS_CD = #{wbsCd}
