mapper.select:
-- retrievePrjClsList 프로젝트 월마감 목록 조회 SELECT * FROM ( SELECT AA.PRJ_CD as prjCd , AA.WBS_CD as wbsCd , AA.deptNm , AA.PRJ_NM as prjNm , AA.PRJ_STR_DT as prjStrDt , AA.PRJ_END_DT as prjEndDt , AA.PGS_STAT_CD as pgsStatCd , AA.pgsStatNm , AA.plEmpNm , AA.PRJ_CLS_YYMM as prjClsYymm , (CASE WHEN AA.prjPgsYn = 'Y' THEN AA.prjClsYn WHEN AA.prjPgsYn = 'N' AND AA.lastClsYymm IS NOT NULL AND AA.lastClsYymm != '' THEN 'Y' ELSE 'N' END ) as prjClsYn , (CASE WHEN AA.prjPgsYn = 'Y' THEN AA.prjClsYnNm WHEN AA.prjPgsYn = 'N' AND AA.lastClsYymm IS NOT NULL AND AA.lastClsYymm != '' THEN '마감완료' ELSE '진행' END ) as prjClsYnNm , AA.DEPT_CD as deptCd , AA.prjPgsYn , AA.lastClsYymm ,AA.frstRgstDt ,AA.lastMdfyDt ,case when befprjClsYymm <> '' then '완료' else '' end as befprjClsYymm ,case when befpgsStatNm = 'ON' then 'On Track' when befpgsStatNm = 'OF' then 'Off Track' else '' end befpgsStatNm FROM ( SELECT A.PRJ_CD , A.WBS_CD , [dbo].[fn_getDeptName](A.DEPT_UPER) as deptNm , A.PRJ_NM , A.PRJ_STR_DT , A.PRJ_END_DT , C.PGS_STAT_CD , dbo.fn_getComDtlNm('PGS_STAT_CD', C.PGS_STAT_CD) as pgsStatNm /*진척상태명*/ , (SELECT SA_NAME FROM IRIS_SSO_USER Y WHERE Y.SA_SABUN_NEW = A.PL_EMP_NO) as "plEmpNm" , C.PRJ_CLS_YYMM , (CASE WHEN C.PRJ_CLS_YN IS NULL OR C.PRJ_CLS_YN= '' THEN 'N' ELSE 'Y' END) as prjClsYn , (CASE WHEN C.PRJ_CLS_YN IS NULL OR C.PRJ_CLS_YN= '' THEN '진행' ELSE '마감완료' END) as prjClsYnNm , A.DEPT_CD , (CASE WHEN SUBSTRING((convert(varchar(20),GETDATE(),23)),1,7) BETWEEN SUBSTRING(A.PRJ_STR_DT,1,7) AND (CASE WHEN A.PRJ_END_DT IS NULL OR A.PRJ_END_DT = '' THEN '9999-12' ELSE SUBSTRING(A.PRJ_END_DT,1,7) END) THEN 'Y' ELSE 'N' END) AS prjPgsYn /* 현재월 프로젝트 진행여부 */ , D.PRJ_CLS_YYMM as lastClsYymm , A.FRST_RGST_DT as frstRgstDt , A.LAST_MDFY_DT as lastMdfyDt , isnull(e.PRJ_CLS_YYMM, '') as befprjClsYymm , isnull(e.PGS_STAT_CD, '') as befpgsStatNm FROM IRIS_PRJ_RSST_MST A LEFT OUTER JOIN IRIS_PRJ_RSST_CLS C /*현재월 프로젝트 월마감*/ ON A.PRJ_CD = C.PRJ_CD AND C.PRJ_CLS_YYMM = SUBSTRING((convert(varchar(20), GETDATE() ,23)),1,7) /*현재월기준으로 가져오기*/ AND C.PRJ_CLS_YN = 'Y' LEFT OUTER JOIN IRIS_PRJ_RSST_CLS D /*최종월 프로젝트 월마감*/ ON A.PRJ_CD = D.PRJ_CD AND D.PRJ_CLS_YYMM IN (SELECT MAX(PRJ_CLS_YYMM) FROM IRIS_PRJ_RSST_CLS WHERE PRJ_CD = D.PRJ_CD) AND D.PRJ_CLS_YN = 'Y' LEFT OUTER JOIN IRIS_PRJ_RSST_CLS e ON e.PRJ_CD = a.PRJ_CD AND e.PRJ_CLS_YYMM =CONVERT(NVARCHAR(7), DATEADD(MM, -1, GETDATE()), 23) WHERE 1=1 AND (A.LAST_MDFY_ID <> 'MIG_DATA' AND A.WBS_CD <> 'R16TS0') -- 기술팀 기술전략팀 제외 and A.TCLG_YN != 'Y' and (a.PRJ_END_DT is null OR a.PRJ_END_DT = '')
AND A.DEPT_CD = ( case when '00206840' = #{_userSabun} then '58189985' when '00207030' = #{_userSabun} then '58204567' else #{_teamDept} end)
) AA WHERE (aa.prjPgsYn ='Y' or (prjPgsYn ='N' and lastClsYymm is not null) )
) a where 1=1
and a.deptNm LIKE'%'+#{_userDeptName}+'%'
ORDER BY a.frstRgstDt desc , a.lastMdfyDt DESC, a.deptNm

mapper.insert:

-- insertPrjRsstCls 프로젝트 월마감 등록
    insert into IRIS_PRJ_RSST_CLS
    (
       PRJ_CD
     , WBS_CD
     , PRJ_CLS_YN
     , PGS_STAT_CD
     , PRJ_CLS_SBC
     , PRJ_CLS_YYMM
     , FNO_PLN
     , ATTC_FIL_ID
     , FRST_RGST_ID
     , FRST_RGST_DT
     , LAST_MDFY_ID
     , LAST_MDFY_DT
    )
    values
    (
       #{prjCd}
     , #{wbsCd}
     , 'Y'
     , #{pgsStatCd}
     , #{prjClsSbc}
     , #{prjClsYymm}
     , #{fnoPln}
     , #{attcFilId}
     , #{_userId}
     , getdate()
     , #{_userId}
     , getdate()
    )

mapper.update:

-- updatePrjRsstCls 프로젝트 월마감 수정
    UPDATE IRIS_PRJ_RSST_CLS
       SET LAST_MDFY_ID = #{_userId}
         , LAST_MDFY_DT = getdate()


         , PGS_STAT_CD = #{pgsStatCd}

         , FNO_PLN = #{fnoPln}
         , PRJ_CLS_SBC = #{prjClsSbc}
         , ATTC_FIL_ID = #{attcFilId}

    WHERE PRJ_CD = #{prjCd}
      AND PRJ_CLS_YYMM = #{prjClsYymm}

mapper.insert:
-- insertPrjClsProg
    insert into iris_prj_cls_prog
    (
     PRJ_CD
    ,WBS_CD
    ,PRJ_CLS_YN
    ,PRJ_CLS_YYMM
    ,TSS_NM
    ,SA_USER_NAME
