    ,TSS_STRT_DD
    ,TSS_FNH_DD
    ,PROGRESSRATE
    ,FRST_RGST_DT
    ,FRST_RGST_ID
    ,LAST_MDFY_DT
    ,LAST_MDFY_ID
    )
    values
    (
     #{prjCd}
    ,#{wbsCd}
    ,'Y'
    ,#{prjClsYymm}
    ,#{tssNm}
    ,#{saUserName}
    ,#{deptName}
    ,#{tssStrtDd}
    ,#{tssFnhDd}
    ,#{progressrateReal}
    ,GETDATE()
    ,#{_userId}
    ,GETDATE()
    ,#{_userId}
    )
