mapper.select:
-- retrieveGenTssPgsWBS WBS 조회 WITH w_WBS AS ( select m.* , case when round(m.arslCd, 0) = 100 then 0 when round(m.arslCd, 0) = 0 then tssPeriod else round(m.tssPeriod - (cast(m.arslCd as float) * m.period / 100), 0) end delay
from (
SELECT TSS_CD AS tssCd --과제코드 , WBS_SN AS wbsSn --WBS일련번호 , PID_SN AS pidSn --PID , DEPTH AS depth --DEPTH , DEPTH_SEQ AS depthSeq --순서 , TSS_NM AS tssNm --과제명 , STRT_DT AS strtDt --시작일 , FNH_DT AS fnhDt --종료일 , STRT_DT AS oldStrtDt , FNH_DT AS oldFnhDt , WGVL AS wgvl --가중치 , sa_sabun_new AS saSabunNew --담당자 , ISNULL(dbo.fn_getNameOnTssPgsStep(TSS_CD, 'USER', sa_sabun_new), SA_SABUN_NAME) AS saSabunNewNm , VRF_YN AS vrfYn --검증 , ARSL_CD AS arslCd --실적코드 , ARSL_CD AS orgArslCd --실적코드 , YLD_ITM_NM AS yldItmNm --산출물명 , YLD_ITM_TXT AS yldItmTxt --산출물내용 , DATALENGTH(YLD_ITM_TXT) AS yldItmTxtLen , KWD_TXT AS kwdTxt --키워드 , ATTC_FIL_ID AS attcFilId --파일ID , (SELECT COUNT(*) FROM iris_com_attc_fil X WHERE X.attc_fil_id = A.ATTC_FIL_ID AND DEL_YN = 'N') AS orgAttcFilCnt , (SELECT COUNT(*) FROM iris_com_attc_fil X WHERE X.attc_fil_id = A.ATTC_FIL_ID AND DEL_YN = 'N') AS attcFilCnt , DATEDIFF(DD, CONVERT(DATE, STRT_DT), CONVERT(DATE, FNH_DT)) + 1 AS period --기간 ,DATEDIFF(DD,convert(DATE,STRT_DT),convert(DATE, FNH_DT))+DATEDIFF(DD,convert(DATE,FNH_DT), getdate()) as tssPeriod , CASE WHEN DEPTH = 0 THEN WBS_SN WHEN DEPTH = 1 THEN PID_SN WHEN DEPTH = 2 THEN (SELECT X.PID_SN FROM IRIS_TSS_GEN_WBS X WHERE X.TSS_CD = #{tssCd} AND X.WBS_SN = A.PID_SN ) WHEN DEPTH = 3 THEN (SELECT Y.PID_SN FROM IRIS_TSS_GEN_WBS Y WHERE Y.TSS_CD = #{tssCd} AND Y.WBS_SN = (SELECT X.PID_SN FROM IRIS_TSS_GEN_WBS X WHERE X.TSS_CD = #{tssCd} AND X.WBS_SN = A.PID_SN)) END AS pidSeq , #{_userId} AS userId FROM IRIS_TSS_GEN_WBS A /*일반과제WBS*/ WHERE A.TSS_CD = #{tssCd} ) m )SELECT DENSE_RANK() OVER( ORDER BY pidSeq ) AS drRow , w.* FROM w_WBS w ORDER BY depthSeq

    	select count(erp_cd) as budgCnt 
    	from   IRIS_SAP_BUDG_S_COST a
		where   erp_cd = #{wbsCd}
		and     left(YYYYMM, 4) = #{yyyy}
