mapper.update:

-- updateOusdCooTssAltrSmry 개요 수정 - 변경사유
    UPDATE IRIS_TSS_OUSD_COO_SMRY  /*대외협력과제개요*/
       SET ALTR_RSON = #{altrRson}
         , ADD_RSON = #{addRson}
         , ALTR_ATTC_FIL_ID = #{altrAttcFilId}     /*변경첨부파일ID*/
         , LAST_MDFY_DT     = GETDATE()
         , LAST_MDFY_ID     = #{userId}
     WHERE TSS_CD = #{tssCd}

mapper.select:
-- retrieveOusdCooTssExpStoa 대외협력과제 비용지급 조회 select wbs_cd as wbsCd ,isnull(round(sum(mm1), 2) , 0) as mm1 ,isnull(round(sum(mm2), 2) , 0) as mm2 ,isnull(round(sum(mm3), 2) , 0) as mm3 ,isnull(round(sum(mm4), 2) , 0) as mm4 ,isnull(round(sum(mm5), 2) , 0) as mm5 ,isnull(round(sum(mm6), 2) , 0) as mm6 ,isnull(round(sum(mm7), 2) , 0) as mm7 ,isnull(round(sum(mm8), 2) , 0) as mm8 ,isnull(round(sum(mm9), 2) , 0) as mm9 ,isnull(round(sum(mm10), 2), 0) as mm10 ,isnull(round(sum(mm11), 2), 0) as mm11 ,isnull(round(sum(mm12), 2), 0) as mm12 ,isnull(round(sum(arsl_exp), 2), 0) as totSum from ( select wbs_Cd ,case when right(a.yyyymm, 2) = '01' then sum(a.ARSL_EXP) end 'mm1' ,case when right(a.yyyymm, 2) = '02' then sum(a.ARSL_EXP) end 'mm2' ,case when right(a.yyyymm, 2) = '03' then sum(a.ARSL_EXP) end 'mm3' ,case when right(a.yyyymm, 2) = '04' then sum(a.ARSL_EXP) end 'mm4' ,case when right(a.yyyymm, 2) = '05' then sum(a.ARSL_EXP) end 'mm5' ,case when right(a.yyyymm, 2) = '06' then sum(a.ARSL_EXP) end 'mm6' ,case when right(a.yyyymm, 2) = '07' then sum(a.ARSL_EXP) end 'mm7' ,case when right(a.yyyymm, 2) = '08' then sum(a.ARSL_EXP) end 'mm8' ,case when right(a.yyyymm, 2) = '09' then sum(a.ARSL_EXP) end 'mm9' ,case when right(a.yyyymm, 2) = '10' then sum(a.ARSL_EXP) end 'mm10' ,case when right(a.yyyymm, 2) = '11' then sum(a.ARSL_EXP) end 'mm11' ,case when right(a.yyyymm, 2) = '12' then sum(a.ARSL_EXP) end 'mm12' ,sum(arsl_exp) as arsl_exp from ( select RANK() OVER (PARTITION BY erp_cd, yyyymm, S_GROUP ORDER BY FRST_RGST_DT DESC) AS OVER_STEP_RANK ,erp_cd as wbs_cd ,yyyymm ,S_GROUP ,convert(numeric(20,2), convert(numeric(20),KO_RSLT)/1000000) as arsl_exp -- ,convert(numeric(20,6), convert(numeric(20),KO_RSLT)) as arsl_exp from IRIS_SAP_BUDG_S_COST where erp_cd in ( select wbs_cd from IRIS_TSS_MGMT_MST where tss_scn_cd ='O' and pgs_step_cd ='PG' ) ) a where a.OVER_STEP_RANK = 1 and a.WBS_CD = #{wbsCd}
AND left(a.yyyymm, 4) = #{searchYear}
group by a.wbs_cd, a.yyyymm ) aa group by aa.wbs_cd

select distinct yyyy
from (
		select left(yyyymm, 4) as yyyy
		from   IRIS_SAP_BUDG_S_COST
		where  erp_cd in ( select wbs_cd
								   from   IRIS_TSS_MGMT_MST
								   where  tss_scn_cd ='O'
								   and     pgs_step_cd ='PG'
								   )
							  
		and   erp_CD =#{wbsCd}
		) a
order by yyyy

mapper.update:
    
    -- updateOusdCooTssExpStoa 대외협력과제 비용지급 수정
		UPDATE IRIS_TSS_OUSD_EXP_STOA              /*대외협력과제비용지급*/
           SET TTL_CASH = (CASE WHEN #{ttlCash} = NULL OR #{ttlCash} = '' THEN NULL ELSE CONVERT(numeric, #{ttlCash}) END)
             , YY_NOS_CASH1 = (CASE WHEN #{yyNosCash1} = NULL OR #{yyNosCash1} = '' THEN NULL ELSE CONVERT(numeric, #{yyNosCash1}) END)
             , YY_NOS_DT1 = #{yyNosDt1}
             , YY_NOS_CASH2 = (CASE WHEN #{yyNosCash2} = NULL OR #{yyNosCash2} = '' THEN NULL ELSE CONVERT(numeric, #{yyNosCash2}) END)
             , YY_NOS_DT2 = #{yyNosDt2}
             , YY_NOS_CASH3 = (CASE WHEN #{yyNosCash3} = NULL OR #{yyNosCash3} = '' THEN NULL ELSE CONVERT(numeric, #{yyNosCash3}) END)
             , YY_NOS_DT3 = #{yyNosDt3}
             , YY_NOS_CASH4 = (CASE WHEN #{yyNosCash4} = NULL OR #{yyNosCash4} = '' THEN NULL ELSE CONVERT(numeric, #{yyNosCash4}) END)
             , YY_NOS_DT4 = #{yyNosDt4}
             , LAST_MDFY_DT = GETDATE()
             , LAST_MDFY_ID = #{userId}
		 WHERE TSS_CD = #{tssCd}

mapper.delete:
    
    -- deleteOusdCooTssExpStoa 대외협력과제 비용지급 삭제
		DELETE FROM IRIS_TSS_OUSD_EXP_STOA              /*대외협력과제비용지급*/
		 WHERE TSS_CD = #{tssCd}


