mapper.update:
-- saveRfpInfo
	MERGE IRIS_OPN_RFP_MST MEM
    	USING ( SELECT #{rfpId} AS RFP_ID ) AS Tab1
		ON  (MEM.RFP_ID = Tab1.RFP_ID)
		WHEN MATCHED THEN
	UPDATE SET	 TITLE 			= #{title}
				,REQ_DATE		= #{reqDate}
				,RSECH_ENGN		= #{rsechEngn}
				,DESC_TCLG		= #{descTclg}
				,EXPT_APPL		= #{exptAppl}
				,MAIN_REQ		= #{mainReq}
				,BENCH_MARK_TCLG= #{benchMarkTclg}
				,COLABO_TCLG 	= #{colaboTclg}
				,TIMELINE  		= #{timeline}
				,COMMENTS		= #{comments}
				,COMPANY_NM		= #{companyNm}
				,PJT_IMG_VIEW 	= #{pjtImgView}
				,RGST_EMP_NO  	= #{_userSabun}
				,LAST_MDFY_DT 	= getdate()
				,LAST_MDFY_ID 	= #{_userId}
	WHEN NOT MATCHED THEN
	INSERT (
		  TITLE
		, REQ_DATE
		, RSECH_ENGN
		, DESC_TCLG
		, EXPT_APPL
		, MAIN_REQ
		, BENCH_MARK_TCLG
		, COLABO_TCLG
		, TIMELINE
		, COMMENTS
		, COMPANY_NM
		, PJT_IMG_VIEW
		, SUBMIT_YN
		, RGST_EMP_NO
		, DEL_YN
		, FRST_RGST_DT
		, FRST_RGST_ID
		, LAST_MDFY_DT
		, LAST_MDFY_ID	)
	 VALUES (
		  #{title}
		, #{reqDate}
		, #{rsechEngn}
		, #{descTclg}
		, #{exptAppl}
		, #{mainReq}
		, #{benchMarkTclg}
		, #{colaboTclg}
		, #{timeline}
		, #{comments}
		, #{companyNm}
		, #{pjtImgView}
		, 'N'
		, #{_userSabun}
		, 'N'
		, getdate()
		, #{_userId}
		, getdate()
		, #{_userId}
		 );

-- deleteRfpInfo
	update IRIS_OPN_RFP_MST
	set    del_yn ='Y'
	      ,FRST_RGST_DT = getdate()
	      ,LAST_MDFY_ID = #{_userId}
    where  RFP_ID = #{rfpId}

-- submitRfpInfo
	update IRIS_OPN_RFP_MST
	set    SUBMIT_YN ='Y'
	      ,FRST_RGST_DT = getdate()
	      ,LAST_MDFY_ID = #{_userId}
    where  RFP_ID = #{rfpId}
