mapper.update:

UPDATE PR_PRSREQ_T  
   SET PRS_FLAG 	= 'D'
 WHERE BANFN_PRS 	= #{banfnPrs}
   AND BNFPO_PRS 	= #{bnfpoPrs}
   AND AFNAM		= #{_userNm}
   AND BEDNR		= #{_userSabun}

mapper.select:
/* retrievePurRqInfo 리스트 조회 */ select a.BANFN_PRS as banfnPrs ,a.BNFPO_PRS as bnfpoPrs ,a.SEQ_NUM as seqNum ,a.S_CODE as sCode ,a.BANFN as banfn ,a.BNFPO as bnfpo ,a.KNTTP as knttp ,a.PSTYP as pstyp ,a.TXZ01 as txz01 ,a.MENGE as menge ,a.MEINS as meins ,a.EEIND as eeind ,a.AFNAM as afnam ,a.MATKL as matkl ,a.WERKS as werks ,a.EKGRP as ekgrp ,a.BEDNR as bednr ,a.PREIS as preis ,a.WAERS as waers ,a.PEINH as peinh ,a.SAKTO as sakto ,(SELECT CODE_NM FROM PR_CODEMASTER_T y WHERE y.CODE = RTRIM(a.SAKTO) AND y.DEL_FLAG ='0' AND y.GROUP_CODE ='10' AND y.REGION_CD =a.region_cd ) as saktoNm ,a.IT_ANLN1 as itAnln1 ,a.IT_DATE as itDate ,a.IT_UNAME as itUname ,a.IT_ACC_DATE as itAccDate ,a.IT_UNAME2 as itUname2 ,a.IT_ACC_OPIN as itAccOpin ,a.ANLN1 as anln1 ,a.ANLN1_CR as anln1Cr ,a.ANLKL as anlkl ,a.TXT50 as txt50 ,a.KOSTL as kostl ,a.ORD44 as ord44 ,a.GDLGRP as gdlgrp ,a.POSID as posid ,a.POST1 as post1 ,a.IMPRF as imprf ,a.IZWEK as izwek ,a.ITEM_TXT as itemTxt ,a.REFUSE_PART as refusePart ,a.REFUSE_REASON as refuseReason ,a.BADATE as badate ,a.ERDATE as erdate ,a.ERNAM_PERNR as ernamPernr ,a.PRS_FLAG as prsFlag ,a.MAKER as maker ,a.VENDOR as vendor ,a.CATALOGNO as catalogno ,a.POSITION as position ,a.USED_CODE as usedCode ,a.MESSAGE as message ,a.APR4_PERNR as apr4Pernr ,a.APR4_PERNRNM as apr4Pernrnm ,a.FKSTL as fkstl ,a.LIFNR_PHONE as lifnrPhone ,a.REGION_CD as regionCd ,a.FIXTURE_DATE as fixtureDate ,a.FIXTURE_UNAME as fixtureUname ,a.CURRENT_STATE as currentState ,a.FIX_REASON as fixReason ,a.INS_REASON as insReason ,a.INS_DATE as insDate ,a.INS_UNAME as insUname ,a.IT_SWINS_DATE as itSwinsDate ,a.IT_UNAME3 as itUname3 ,a.IT_REASON as itReason ,a.IT_SWINS_YN as itSwinsYn ,a.MATNR as matnr ,a.BIZ_CD as bizCd ,a.IT_SWREQ_DATE as itSwreqDate ,a.ATTC_FIL_ID as attcFilId ,(select count(*) from [dbo].[IRIS_COM_ATTC_FIL] b where b.ATTC_FIL_ID = a.ATTC_FIL_ID and b.MENU_TYPE = 'prsPolicy' and b.DEL_YN = 'N') fileCnt from [dbo].[PR_PRSREQ_T] a where BANFN_PRS = cast(#{banfnPrs} as int)
and a.USED_CODE = #{usedCode}
and a.PRS_FLAG = #{prsFlag}

	/* bnfpoPrs 번호채번 */
		SELECT ISNULL(MAX(BNFPO_PRS), 0) + 1 
               FROM PR_PRSREQ_T
         WHERE BANFN_PRS = #{banfnPrs}

/* retrieveMyPurRqList 리스트 조회 */ select CONVERT(varchar(10), a.BADATE, 23) as badate ,a.BANFN_PRS as banfnPrs ,a.BNFPO_PRS as bnfpoPrs ,a.SEQ_NUM as seqNum ,a.S_CODE as sCode ,a.BANFN as banfn ,a.BNFPO as bnfpo ,a.KNTTP as knttp ,a.PSTYP as pstyp ,a.TXZ01 as txz01 ,a.MENGE as menge ,a.MEINS as meins ,a.EEIND as eeind ,a.AFNAM as afnam ,a.MATKL as matkl ,a.WERKS as werks ,(SELECT CODE_NM FROM [dbo].[PR_CODEMASTER_T] z WHERE z.CODE = RTRIM(a.WERKS) AND z.DEL_FLAG = 0 AND z.GROUP_CODE = '02' AND z.REGION_CD = a.region_cd ) as werksnm ,a.EKGRP as ekgrp ,(SELECT CODE_NM FROM [dbo].[PR_CODEMASTER_T] x WHERE x.CODE = RTRIM(a.EKGRP) AND x.DEL_FLAG = '0' AND x.GROUP_CODE = '01' AND x.REGION_CD = a.region_cd ) as ekgrpnm ,a.BEDNR as bednr ,a.PREIS as preis ,a.WAERS as waers ,a.PEINH as peinh ,a.SAKTO as sakto ,(SELECT CODE_NM FROM [dbo].[PR_CODEMASTER_T] y WHERE y.CODE = RTRIM(a.SAKTO) AND y.DEL_FLAG ='0' AND y.GROUP_CODE ='10' AND y.REGION_CD =a.region_cd ) as saktonm ,a.ANLKL as anlkl ,a.TXT50 as txt50 ,a.KOSTL as kostl ,a.POSID as posid ,a.POST1 as posidnm ,a.ITEM_TXT as itemTxt ,a.PRS_FLAG as prsFlag ,a.BIZ_CD as bizCd ,a.POSITION as position ,a.MAKER as maker ,a.VENDOR as vendor ,a.CATALOGNO as catalogno ,a.ATTC_FIL_ID as attcFilId ,(select count(*) from [dbo].[IRIS_COM_ATTC_FIL] b where b.ATTC_FIL_ID = a.ATTC_FIL_ID and b.MENU_TYPE = 'prsPolicy' and b.DEL_YN = 'N') fileCnt from [dbo].[PR_PRSREQ_T] a where 1 = 1 and a.AFNAM = #{_userNm} and a.BEDNR = #{_userSabun} and a.PRS_FLAG <> 'D' and a.EEIND between REPLACE(#{fromPurRqDt},'-','') and REPLACE(#{toPurRqDt},'-','') and CONVERT(varchar(8), a.BADATE, 112) between REPLACE(#{fromRegDt},'-','') and REPLACE(#{toRegDt},'-','')
and a.PRS_FLAG = #{prsFlag}
and a.TXZ01 = #{itemnm}
and a.POSID = #{wbsCd}
and a.EKGRP = #{ekgrp}
order by a.SEQ_NUM desc

/* 구매요청 결재의뢰 조회 */ SELECT BANFN_PRS as banfnPrs , APR1_PERNR as apr1Pernr , APR1_ENAME as apr1Ename , APR2_PERNR as apr2Pernr , APR2_ENAME as apr2Ename , APR3_PERNR as apr3Pernr , APR3_ENAME as apr3Ename , APR4_PERNR as apr4Pernr , APR4_ENAME as apr4Ename , G_CHECK as gCheck , G_CHECK2 as gCheck2 , OPIN_DOC as opinDoc , WBS_PERNR as wbsPernr , (SELECT DISTINCT BEDNR FROM PR_PRSREQ_T as a WHERE a.BANFN_PRS = #{banfnPrs}) as bednr FROM PR_APPROVAL_T WHERE BANFN_PRS = #{banfnPrs}
AND APR1_PERNR = #{apr1Pernr}
AND APR2_PERNR = #{apr2Pernr}
AND APR3_PERNR = #{apr3Pernr}
AND APR4_PERNR = #{apr4Pernr}

mapper.update:
		
		UPDATE PR_PRSREQ_T SET
			   BANFN     = #{banfn},
			   BNFPO     = #{bnfpo},
			   ANLN1     = #{anln1},
			   POSID     = #{posid},
			   PRS_FLAG  = #{prsFlag},
			   MESSAGE   = #{message}
		 WHERE BANFN_PRS = #{banfnPrs}
		   AND BNFPO_PRS = #{bnfpoPrs}
		
		
           AND SEQ_NUM =#{seqNum}
