mapper.select:
-- rlabTeamGateList team gate 조회 SELECT TEAM_GATE_ID AS teamGateId ,TITL AS titl ,SBC AS sbc ,CMPL_YN AS cmplYn ,CASE WHEN CMPL_YN='Y' THEN '종료' ELSE '진행중' END AS cmplNm ,ATTC_FIL_ID AS attcFilId ,DEL_YN AS delYn ,convert(nvarchar(10), FRST_RGST_DT, 23) AS frstRgstDt ,FRST_RGST_ID AS frstRgstId ,dbo.fn_getUserName(FRST_RGST_ID) AS frstRgstNm ,(SELECT COUNT(1) FROM IRIS_TEAM_GATE_DTL DTL WHERE DTL.TEAM_GATE_ID=A.TEAM_GATE_ID) AS evCnt FROM IRIS_TEAM_GATE_MST A WHERE 1=1 AND DEL_YN='N'
AND CMPL_YN=#{cmplYn}
AND TITL LIKE '%'+#{titl}+'%'

mapper.insert:
	-- insertRlabTeamGate
	INSERT INTO dbo.IRIS_TEAM_GATE_MST
        (TITL
		,SBC
		,ATTC_FIL_ID
		,FRST_RGST_ID
		,LAST_MDFY_ID
		)
    VALUES
        (#{titl}
		,#{sbc}
		,#{attcFilId}
        ,#{_userId}  -- 세션 사번
        ,#{_userId}  -- 세션 사번
        )

mapper.update:
	
	-- saveTempRlabTeamGateEv team gate 임시저장
        MERGE IRIS_TEAM_GATE_DTL A
         USING  (SELECT #{teamGateId}   AS TEAM_GATE_ID , #{_userId} AS FRST_RGST_ID) AS B
            ON  A.TEAM_GATE_ID  	= B.TEAM_GATE_ID
			AND A.FRST_RGST_ID  	= B.FRST_RGST_ID
          WHEN NOT MATCHED THEN
        INSERT
             (  TEAM_GATE_ID
             ,  EV1
             ,  EV2
             ,  EV3
             ,  EV4
             ,  EV5
             ,  EV6
             ,  EV7
             ,  EV8
             ,  EV9
             ,  EV10
             ,  FRST_RGST_ID
             ,  LAST_MDFY_ID
            )
        VALUES
             (  #{teamGateId}
             ,  #{ev1}
             ,  #{ev2}
             ,  #{ev3}
             ,  #{ev4}
             ,  #{ev5}
             ,  #{ev6}
             ,  #{ev7}
             ,  #{ev8}
             ,  #{ev9}
             ,  #{ev10}
             ,  #{_userId}
             ,  #{_userId}
             )
          WHEN MATCHED THEN
        UPDATE
           SET A.EV1 	  = #{ev1}
             , A.EV2 	  = #{ev2}
             , A.EV3 	  = #{ev3}
             , A.EV4 	  = #{ev4}
             , A.EV5 	  = #{ev5}
             , A.EV6 	  = #{ev6}
             , A.EV7 	  = #{ev7}
             , A.EV8 	  = #{ev8}
             , A.EV9 	  = #{ev9}
             , A.EV10 	  = #{ev10}
             , A.LAST_MDFY_DT = GETDATE()
             , A.LAST_MDFY_ID = #{_userId}
             ;

	
	-- saveRlabTeamGateEv team gate 투표저장
        MERGE IRIS_TEAM_GATE_DTL A
         USING  (SELECT #{teamGateId}   AS TEAM_GATE_ID , #{_userId} AS FRST_RGST_ID) AS B
            ON  A.TEAM_GATE_ID  	= B.TEAM_GATE_ID
			AND A.FRST_RGST_ID  	= B.FRST_RGST_ID
          WHEN NOT MATCHED THEN
        INSERT
             (  TEAM_GATE_ID
             ,  EV1
             ,  EV2
             ,  EV3
             ,  EV4
             ,  EV5
             ,  EV6
             ,  EV7
             ,  EV8
             ,  EV9
             ,  EV10
             ,  EV_CMPL_YN
             ,  FRST_RGST_ID
             ,  LAST_MDFY_ID
            )
        VALUES
             (  #{teamGateId}
             ,  #{ev1}
             ,  #{ev2}
             ,  #{ev3}
             ,  #{ev4}
             ,  #{ev5}
             ,  #{ev6}
             ,  #{ev7}
             ,  #{ev8}
             ,  #{ev9}
             ,  #{ev10}
             ,  'Y'
             ,  #{_userId}
             ,  #{_userId}
             )
          WHEN MATCHED THEN
        UPDATE
           SET A.EV1 	  = #{ev1}
             , A.EV2 	  = #{ev2}
             , A.EV3 	  = #{ev3}
             , A.EV4 	  = #{ev4}
             , A.EV5 	  = #{ev5}
             , A.EV6 	  = #{ev6}
             , A.EV7 	  = #{ev7}
             , A.EV8 	  = #{ev8}
             , A.EV9 	  = #{ev9}
             , A.EV10 	  = #{ev10}
             , A.EV_CMPL_YN = 'Y'
             , A.LAST_MDFY_DT = GETDATE()
             , A.LAST_MDFY_ID = #{_userId}
             ;

	
	-- saveRlabTeamGateEvCmpl team gate 투표완료
		UPDATE IRIS_TEAM_GATE_MST
		   SET TITL = #{titl}
			  ,SBC  = #{sbc}
			  ,CMPL_YN = 'Y'
			  ,ATTC_FIL_ID  = #{attcFilId}
			  ,LAST_MDFY_ID = #{_userId}
			  ,LAST_MDFY_DT = GETDATE()
	

	
		 WHERE 1=1
		   AND TEAM_GATE_ID = #{teamGateId}

	
	-- saveRlabTeamGateEvReg team gate 투표수정
		UPDATE IRIS_TEAM_GATE_MST
		   SET TITL = #{titl}
			  ,SBC  = #{sbc}
			  ,ATTC_FIL_ID  = #{attcFilId}
			  ,LAST_MDFY_ID = #{_userId}
			  ,LAST_MDFY_DT = GETDATE()
	

	
		 WHERE 1=1
		   AND TEAM_GATE_ID = #{teamGateId}
