mapper.insert:
 	
   	INSERT INTO IRIS_FXAINFO_IF
   	(
   	 	 ASSET_NO			
		,ASSET_NM			
		,CCTR_CD			
		,ASSET_CLAS		
		,ASSET_TEXT		
		,ACQR_DT			
		,REDM_RATE		
		,ACQR_BEF			
		,ACQR_ICDC		
		,ACQR_BAL			
		,APPR_BEF			
		,APPR_ICDC		
		,APPR_BAL			
		,THTERM_REDM	
		,THMM_REDM		
		,EXSI_YYS			
		,BOOK_AMT			
		,INVSM_CD			
		,BIZ_TRTY			
		,WBS1					
		,RULE1				
		,WBS2					
		,RULE2				
		,WBS3					
		,RULE3				
		,PERNR				
		,ENAME				
		,BWASL				
		,DEAKT				
		,MENGE				
		,MEINS				
		,FRST_RGST_DT
		,FRST_RGST_ID
		,LAST_MDFY_DT
		,LAST_MDFY_ID
   	)
   	VALUES
   	(
		 #{assetNo}		
		,#{assetNm}		
		,#{cctrCd}		
		,convert(nvarchar(4), cast(#{assetClas} AS INT))	
		,#{assetText}	
		,#{acqrDt}		
		,#{redmRate}	
		,#{acqrBef}		
		,#{acqrIcdc}	
		,#{acqrBal}		
		,#{apprBef}		
		,#{apprIcdc}	
		,#{apprBal}		
		,#{thtermRedm}
		,#{thmmRedm}	
		,#{exsiYys}		
		,#{bookAmt}		
		,#{invsmCd}		
		,#{bizTrty}		
		,#{wbs1}			
		,#{rulE1}			
		,#{wbs2}			
		,#{rule2}			
		,#{wbs3}			
		,#{rule3}			
		,#{pernr}			
		,#{ename}			
		,#{bwasl}			
		,#{deakt}			
		,#{menge}			
		,#{meins}		
		,GETDATE()
		,'BATCH'
		,GETDATE()
		,'BATCH'
   	)

mapper.update:
 	
   		MERGE IRIS_FXA_INFO_MST MEM
			USING ( SELECT #{fxaNo} AS FXA_NO ) AS Tab1
			ON  (MEM.FXA_NO = Tab1.FXA_NO)
			WHEN MATCHED THEN
	UPDATE SET	 FXA_NM       = #{fxaNm}
	            ,FXA_ST_CD    = #{fxaStCd}
				,CRGR_ID	  = #{crgrId}
				,WBS_CD	      = #{wbsCd}
				,FXA_CLSS	  = #{fxaClss}
				,FXA_QTY	  = #{fxaQty}
				,FXA_UTM_NM	  = #{fxaUtmNm}
				,OBT_PCE      = #{obtPce}
				,BKP_PCE 	  = #{bkpPce}
				,OBT_DT		  = #{obtDt}
				,USE_USF	  = #{useUsf}
				,DSU_DT	  	  = #{dsuDt}
				,DEPT_CD      = #{deptCd}
				,LAST_MDFY_DT = GETDATE()
				,LAST_MDFY_ID = 'batch'
		WHEN NOT MATCHED THEN
		INSERT (
		         FXA_NM
				,FXA_NO
				,FXA_ST_CD
				,WBS_CD
				,PRJ_CD
				,CRGR_ID
				,FXA_CLSS
				,FXA_QTY
				,FXA_UTM_NM
				,OBT_PCE
				,BKP_PCE
				,OBT_DT
				,DSU_DT
				,USE_USF
				,DEPT_CD 
				,FRST_RGST_DT
				,FRST_RGST_ID
				,LAST_MDFY_DT
				,LAST_MDFY_ID)
	       VALUES (
			     #{fxaNm}
				,#{fxaNo}
				,#{fxaStCd}
				,#{wbsCd}
				,#{prjCd}
				,#{crgrId}
				,#{fxaClss}
				,#{fxaQty}
				,#{fxaUtmNm}
				,#{obtPce}
				,#{bkpPce}
				,#{obtDt}
				,#{dsuDt}
				,#{useUsf}
				,#{deptCd}
				,GETDATE()
				,'batch'
				,GETDATE()
				,'batch'
			);
