mapper.select:
/* spaceRqprList 리스트 조회 */ SELECT RQPR_ID AS rqprId, ACPC_NO AS acpcNo, --접수번호 dbo.fn_getComDtlNm('SPACE_SCN_CD',SPACE_SCN_CD) AS spaceScnNm, --평가목적 SPACE_NAME AS spaceNm, --평가명 RGST_ID AS rgstId, --등록자 dbo.fn_getSpaceCtrgNames(RQPR_Id) AS CtrgNames, --평가카테고리 dbo.fn_getSpacePrvsNames(RQPR_Id) AS PrvsNames, --평가항목 dbo.fn_getUserName(RGST_ID) AS rgstNm, --의뢰자 RQPR_DT AS rqprDt, --의뢰일자 ACPC_DT AS acpcDt, --접수일자 CMPL_PARR_DT AS cmplParrDt, --완료예정일 CMPL_DT AS cmplDt, --완료일 dbo.fn_getComDtlNm('ANL_UGY_YN' , SPACE_UGY_YN) AS spaceUgyYnNm,--긴급유무 dbo.fn_getSpaceRqprCrgrNames(RQPR_ID) AS spaceChrgNm, --평가담당자 dbo.fn_getComDtlNm('OPPB_SCP_CD' , OPPB_SCP_CD) AS oppbScpCd, --공개여부(비밀여부) dbo.fn_getComDtlNm('ACPC_ST_CD' , ACPC_ST_CD) AS acpcStNm --상태 FROM IRIS_SPACE_RQPR_MST mst
WHERE 1 = 1
AND RQPR_ID IN (SELECT SP.RQPR_ID FROM IRIS_SPACE_PROD SP WHERE SP.EV_CTGR0 = #{cmbCtgr0Cd})
AND WBS_CD = #{wbsCd}
AND RQPR_DT between #{fromRqprDt} AND #{toRqprDt}
AND SPACE_NAME like '%${spaceNm}%'
AND RGST_ID IN (SELECT SA_USER FROM IRIS_SSO_USER WHERE SA_NAME like '%${rgstNm}%')
AND RQPR_DEPT_CD = #{rqprDeptCd}
AND RQPR_ID IN (SELECT RQPR_ID FROM IRIS_SPACE_WAY_CRGR WHERE INFM_PRSN_ID IN (SELECT SA_USER FROM IRIS_SSO_USER WHERE SA_NAME like '%${spaceChrgNm}%'))
AND ACPC_NO like '${acpcNo}%'
AND ACPC_ST_CD = #{acpcStCd}
AND SPACE_CHRG_ID = #{spaceChrgId}
AND RGST_ID = #{rgstId}
AND (MST.RQPR_DEPT_CD = #{_userDept} or MST.RQPR_ID in (select rqpr_id from IRIS_SPACE_RQPR_INFM where del_yn ='N' and infm_prsn_id = #{_userId} ))
AND ACPC_ST_CD != '00'
AND DEL_YN = 'N' ORDER BY RQPR_ID DESC

mapper.insert:
		/*insertSpaceRqpr 평가의뢰 마스터 등록 */
		insert into IRIS_SPACE_RQPR_MST(
			 RQPR_DT				--의뢰일자
			,RGST_ID				--등록자ID
			,RQPR_DEPT_CD			--의뢰자부서코드
			,RQPR_TEAM_CD			--의뢰자팀코드
			,SPACE_NAME				--평가명
			,SPACE_SCN_CD			--평가구분코드
			,SPACE_UGY_YN			--긴급유무코드
			,SPACE_SBC				--평가목적
			,RQPR_ATTC_FILE_ID		--의뢰첨부파일ID
			,FRST_RGST_ID			--최초등록자
			,LAST_MDFY_ID			--최종수정자
			,OPPB_SCP_CD			--공개범위 코드
			,SCRT_RSON				--비밀 사유
			,WBS_CD					--WBS코드
			,EV_SUBJ_NM				--평가 대상명
			,SBMP_CD				--제출처 코드
			,SBMP_NM				--제출처 명
			,QTAS_DPST				--정량지표
			,QNAS_DPST				--정성지표
			,GOAL_PFMC				--목표성능
			,RSLT_DPST				--결과지표
			,EV_CASES				--평가Class
			,EV_SUBJ_DTL			--평가대상 상세
			,T_CLOUD				--T-CLOUD URL
		)values(
			Convert(varchar(10), Current_Timestamp, 120)
			,#{userId}
			,#{userDeptCd}
			,#{userTeamCd}
			,#{spaceNm}
			,#{spaceScnCd}
			,#{spaceUgyYn}
			,#{spaceSbc}
			,#{rqprAttcFileId}
			,#{userId}
			,#{userId}
			,#{oppbScpCd}
			,#{scrtRson}
			,#{spaceRqprWbsCd}
			,#{evSubjNm}
			,#{sbmpCd}
			,#{sbmpNm}
			,#{qtasDpst}
			,#{qnasDpst}
			,#{goalPfmc}
			,#{rsltDpst}
			,#{evCases}
			,#{evSubjDtl}
			,#{tCloud}
		)

mapper.update:
/* updateSpaceRqpr 평가 마스터 저장 */ update IRIS_SPACE_RQPR_MST set space_name = #{spaceNm}, --평가명 space_sbc = #{spaceSbc}, --평가목적 space_scn_cd = #{spaceScnCd}, --평가구분코드 wbs_cd = #{spaceRqprWbsCd}, --WBS코드 space_ugy_yn = #{spaceUgyYn}, --긴급유무코드 oppb_scp_cd = #{oppbScpCd}, --공개범위 코드 scrt_rson = #{scrtRson}, --비밀사유 ev_subj_nm = #{evSubjNm}, --평가 대상명 sbmp_cd = #{sbmpCd}, --제출처 코드 sbmp_nm = #{sbmpNm}, --제출처 명 qtas_dpst = #{qtasDpst}, --정량지표 qnas_dpst = #{qnasDpst}, --정성지표 goal_pfmc = #{goalPfmc}, --목표성능 rslt_dpst = #{rsltDpst}, --결과지표 ev_cases = #{evCases}, --평가Class ev_subj_dtl = #{evSubjDtl}, --평가대상 상세 t_cloud = #{tCloud}, --T-CLOUD URL acpc_st_cd = #{acpcStCd}, --접수상태코드 cmpl_parr_dt = #{cmplParrDt}, --완료예정일 rqpr_attc_file_id = #{rqprAttcFileId}, --의뢰첨부파일ID
acpc_no = (select 'H' + substring(cast(year(getdate()) as nvarchar(4)), 3, 2) + '-' + RIGHT('0000' + CAST(count(1) + 1 AS VARCHAR(4)), 4) from iris_space_rqpr_mst where acpc_no like 'R' + substring(cast(year(getdate()) as nvarchar(4)), 3, 2) + '%'), acpc_dt = Convert(varchar(10), getdate(), 120),
last_mdfy_dt = getdate(), last_mdfy_id = #{userId} --최종수정자 where rqpr_id = ${rqprId}

	-- updateSpaceRqprDelYn
		update	iris_space_rqpr_mst
		set		del_yn = 'Y',
				last_mdfy_dt = getdate(),
				last_mdfy_id = #{_userId}
		where	rqpr_id = ${rqprId}

mapper.insert:
		/*insertSpaceRqprWayCrgr 평가방법 등록*/
		INSERT INTO IRIS_SPACE_WAY_CRGR(
			 RQPR_ID		--의뢰ID
			,EV_CTGR		--평가카테고리
			,EV_PRVS		--평가항목
			,INFM_PRSN_ID	--담당자
			,FRST_RGST_ID	--최초등록자
			,LAST_MDFY_ID	--최종수정자
		)values(
			 #{rqprId}
    		,#{evCtgr}
    		,#{evPrvs}
    		,#{infmPrsnId}
    		,#{userId}
    		,#{userId}
		)

mapper.update:
		/* updateSpaceRqprWayCrgr 평가정보 수정*/
		UPDATE	IRIS_SPACE_WAY_CRGR
		SET		EV_CTGR			= #{evCtgr},	--평가카테고리
				EV_PRVS			= #{evPrvs},	--평가항목
				INFM_PRSN_ID	= #{infmPrsnId},--담당자
				last_mdfy_dt	= getdate(),
				last_mdfy_id	= #{userId}
		WHERE	RQPR_ID = ${rqprId}

	-- updateSpaceRqprSmpoDelYn
		/* updateSpaceRqprWayCrgrDelYn 평가정보 삭제*/
		UPDATE	IRIS_SPACE_WAY_CRGR
		SET		DEL_YN = 'Y',
				LAST_MDFY_DT = getdate(),
				LAST_MDFY_ID = #{userId}
		WHERE	RQPR_ID = ${rqprId}

mapper.insert:
		/* insertSpaceRqprProd 제품군 등록*/
		INSERT INTO IRIS_SPACE_PROD(
			 RQPR_ID		--의뢰ID
