			,FRST_RGST_ID	--최초등록자
			,LAST_MDFY_ID	--최종수정자
		)VALUES(
			 #{rqprId}
			,#{evCtgr0}
			,#{evCtgr1}
			,#{evCtgr2}
			,#{evCtgr3}
			,#{userId}
			,#{userId}
		)

mapper.update:
		/* updateSpaceRqprProd 제품군 수정*/
		UPDATE	IRIS_SPACE_PROD
		SET		EV_CTGR0		= #{evCtgr0},	--사업부
				EV_CTGR1		= #{evCtgr1},	--제품군
				EV_CTGR2		= #{evCtgr2},	--분류
				EV_CTGR3		= #{evCtgr3},	--제품
				last_mdfy_dt	= getdate(),
				last_mdfy_id	= #{userId}
		WHERE	RQPR_ID = ${rqprId}

		/* updateSpaceRqprProdDelYn 제품군 삭제*/
		UPDATE	IRIS_SPACE_PROD
		SET		DEL_YN = 'Y',
				LAST_MDFY_DT = getdate(),
				LAST_MDFY_ID = #{userId}
		WHERE	RQPR_ID = ${rqprId}

