mapper.select:

	-- getExprWay 개발방법 조회
SELECT EXPR_WAY as exprWay
FROM  IRIS_SPACE_RQPR_EXPR
WHERE   RQPR_EXPR_ID = ${rqprExprId}
AND     RQPR_ID = #{rqprId}
