mapper.select:
        /* ID/PW 조회   sql-login.xml  / irisLogin /  evalUser */
		SELECT  SU.sa_user
		FROM IRIS_SSO_USER SU
		WHERE SU.SA_USER = #{eeId}
		AND   su.sso_ex_flag != 'D'

mapper.update:
  	/* updateSecurityInfo 개인정보 수집이용/위탁 동의 여부 업데이트  (iris 처리)*/
		UPDATE WOASIS.TB_USER_MST
		   SET SEC_CONF_YN_WINS = #{seq1}||#{seq2}
		     , SEC_CONF_DT_WINS = SYSDATE
		 WHERE SUBSTR(XCMK_CD, 2) = #{xcmkCd}
		   AND EE_ID = #{eeId}

  	/* updateVowInfo 개인정보보호 서약서 업데이트  (iris 처리)*/
		UPDATE WOASIS.TB_USER_MST
		   SET SEC_VOW_YN_WINS = #{vow}
		     , SEC_VOW_DT_WINS = SYSDATE
		 WHERE SUBSTR(XCMK_CD, 2) = #{xcmkCd}
		   AND EE_ID = #{eeId}

mapper.select:
  	/* retrievePassDt 패스워드 변경일 조회*/
      SELECT COUNT(1) AS "cnt"
	   FROM WOASIS.TB_USER_MST
	  WHERE XCMK_CD = #{xcmkCd}
	    AND EE_ID = #{eeId}
	    AND SYSDATE - 90 > NVL(PW_MDFY_DT, SYSDATE-99)

mapper.update:
 /* updateLoginInfo 최근 로그인 시간, 로그인 횟수 업데이트 */
	UPDATE WOASIS.TB_USER_MST
   	   SET LGI_OFT = NVL(LGI_OFT,0) + 1
       	 , LAST_LOGIN_DT = SYSDATE
       	 , LOGIN_FAIL_CNT = 0
       	 , LOGIN_LOCK = 'N'
 	WHERE XCMK_CD = #{xcmkCd}
      AND EE_ID = #{eeId}

mapper.select:
  	/* loginFailureCount 로그인 실패 기록 및 횟수 리턴*/
	    	{call LOGIN_FAILURE_COUNT(
	    	   #{xcmkCd, jdbcType=VARCHAR, mode=IN},
	    	   #{eeId, jdbcType=VARCHAR, mode=IN},
			   #{lock, jdbcType=VARCHAR, mode=OUT, javaType=String},
			   #{failCnt, jdbcType=VARCHAR, mode=OUT, javaType=String},
			   #{returnCode, jdbcType=VARCHAR, mode=OUT, javaType=String}
	        )}

/* retrieveUserDetl 사용자 상세정보*/ SELECT EE_ID as "eeId" /*사원ID*/ , EE_NM as "eeNm" /*사원명*/ , XCMK_CD as "xcmkCd" /*거래선코드*/ , PW2 as "pw" /*비밀번호*/ , TWMS_USE_YN as "winsUseYn" /*윈스 사용여부*/ , USE_YN as "woasisUseYn" /* 웹오아시스 사용여부*/ , OPS_NM as "opsNm" /*부서명*/ , POA_NM as "poaNm" /*직위명*/ , TO_DATE(ETCP_DT, 'YYYY-MM-DD') as "etcpDt" /* 입사일자*/ , TO_DATE(RETR_DT, 'YYYY-MM-DD') as "retrDt" /*퇴사일자*/ , TO_DATE(BRTH_DT, 'YYYY-MM-DD') as "brthDt" /*출생일자*/ , LNC_SOCL_CD as "lncSoclCd" /*음력양력코드*/ , TO_DATE(MARI_CMMO_DT, 'YYYY-MM-DD') as "mariCmmoDt" /*결혼기념일자*/ , SUBSTR(POST_NO,1,3) ||'-'|| SUBSTR(POST_NO,4,3) as "postNo" /*우편번호*/ , POST_NO_ADR as "postNoAdr" /*우편번호주소*/ , DTL_ADR as "dtlAdr" /*상세주소*/ , EMAIL_NM as "emailNm" /*EMAIL명*/ , TRIM(SUBSTR(CRRA_TEL_NO, 1, 4)) AS "crraTelNo1" /*휴대전화번호1*/ , TRIM(SUBSTR(CRRA_TEL_NO, 5, 4)) AS "crraTelNo2" /*휴대전화번호2*/ , TRIM(SUBSTR(CRRA_TEL_NO, 9, 4)) AS "crraTelNo3" /*휴대전화번호3*/ , TRIM(SUBSTR(HOME_TEL_NO, 1, 4)) AS "homeTelNo1" /*자택전화번호1*/ , TRIM(SUBSTR(HOME_TEL_NO, 5, 4)) AS "homeTelNo2" /*자택전화번호2*/ , TRIM(SUBSTR(HOME_TEL_NO, 9, 4)) AS "homeTelNo3" /*자택전화번호3*/ , TWMS_AUTH_G_CD as "twmsAuthGCd" , RGST_YN as "rgstYn" , NVL(PRI_POL_YN, 'N') AS "priPolYn" /*개인정보수집동의여부*/ FROM WOASIS.TB_USER_MST
WHERE EE_ID = #{eeId}
AND XCMK_CD= #{_xcmkCd}

/* retrieveUserAuthGCdLData 사용자가 속한 업체의 권한그룹코드 조회*/
	SELECT TWMS_AUTH_G_CD   as "twmsAuthGCd"
	  FROM WOASIS.TB_USER_MST
	 WHERE XCMK_CD = #{userXcmkCd}
	   AND EE_ID = 'master'

mapper.update:
     /* updateContractInfo 품질 관리 약정 업데이트 */
	UPDATE HOUSING.TB_GU106
	   SET AGRE_YENO = #{agreYn}
	     , AGRE_DATE = SYSDATE
	 WHERE CUST_CODE = #{xcmkCd}
