mapper.update:

-- saveCodeInfo 공통코드 등록, 수정
MERGE IRIS_ADM_COM_CD MEM
    USING ( SELECT #{comId} AS COM_ID ) AS Tab1
    ON  (MEM.COM_ID = Tab1.COM_ID)
    WHEN MATCHED THEN
UPDATE
   SET   COM_CD_EXPL      = #{comCdExpl}
        ,COM_DTL_NM       = #{comDtlNm}
        ,COM_ORD          = #{comOrd}
        ,DEL_YN           = #{delYn}
        ,LAST_MDFY_DT     = GETDATE()
        ,LAST_MDFY_ID     = #{_userId}
        
        
            ,BATCH_EXEC_DT = GETDATE()
        
        
WHEN NOT MATCHED THEN
 INSERT (
         COM_CD_CD
        ,COM_CD_NM
        ,COM_CD_EXPL
        ,COM_DTL_CD
        ,COM_DTL_NM
        ,DEL_YN
        ,FRST_RGST_DT
        ,FRST_RGST_ID
        ,LAST_MDFY_DT
        ,LAST_MDFY_ID)
   VALUES (
         #{comCdCd}
        ,#{comCdNm}
        ,#{comCdExpl}
        ,#{comDtlCd}
        ,#{comDtlNm}
        ,'N'
        ,GETDATE()
        ,#{_userId}
        ,GETDATE()
        ,#{_userId}
    );
